from typing import Optional, List, Dict, Any
from dataclasses import dataclass
from enum import Enum
from playwright.async_api import Page, Locator
from .config import SelectorStrategy
from .logger import get_logger

logger = get_logger()


@dataclass
class SelectorOptions:
    strategy: SelectorStrategy = SelectorStrategy.AUTO
    timeout: int = 10000
    state: str = "visible"
    strict: bool = False


class SmartSelector:

    @staticmethod
    async def find_element(
        page: Page,
        selector: str,
        options: Optional[SelectorOptions] = None
    ) -> Optional[Locator]:
        if options is None:
            options = SelectorOptions()

        if options.strategy == SelectorStrategy.AUTO:
            return await SmartSelector._auto_select(page, selector, options)
        elif options.strategy == SelectorStrategy.CSS:
            return await SmartSelector._css_select(page, selector, options)
        elif options.strategy == SelectorStrategy.XPATH:
            return await SmartSelector._xpath_select(page, selector, options)
        elif options.strategy == SelectorStrategy.TEXT:
            return await SmartSelector._text_select(page, selector, options)
        elif options.strategy == SelectorStrategy.ARIA:
            return await SmartSelector._aria_select(page, selector, options)

    @staticmethod
    async def _auto_select(page: Page, selector: str, options: SelectorOptions) -> Optional[Locator]:
        strategies = [
            (SelectorStrategy.CSS, SmartSelector._css_select),
            (SelectorStrategy.XPATH, SmartSelector._xpath_select),
            (SelectorStrategy.TEXT, SmartSelector._text_select),
            (SelectorStrategy.ARIA, SmartSelector._aria_select),
        ]

        for strategy_type, strategy_func in strategies:
            try:
                logger.debug(f"Trying {strategy_type.value} selector: {selector}")
                element = await strategy_func(page, selector, options)
                if element:
                    count = await element.count()
                    if count > 0:
                        logger.success(f"Found element using {strategy_type.value} selector")
                        return element
            except Exception as e:
                logger.debug(f"{strategy_type.value} selector failed: {str(e)}")
                continue

        logger.warning(f"Could not find element with any strategy: {selector}")
        return None

    @staticmethod
    async def _css_select(page: Page, selector: str, options: SelectorOptions) -> Locator:
        return page.locator(selector)

    @staticmethod
    async def _xpath_select(page: Page, selector: str, options: SelectorOptions) -> Locator:
        if not selector.startswith('//') and not selector.startswith('('):
            selector = f"//{selector}"
        return page.locator(f"xpath={selector}")

    @staticmethod
    async def _text_select(page: Page, selector: str, options: SelectorOptions) -> Locator:
        return page.get_by_text(selector)

    @staticmethod
    async def _aria_select(page: Page, selector: str, options: SelectorOptions) -> Locator:
        return page.get_by_role(selector)

    @staticmethod
    async def find_all(
        page: Page,
        selector: str,
        options: Optional[SelectorOptions] = None
    ) -> List[Locator]:
        locator = await SmartSelector.find_element(page, selector, options)
        if locator:
            count = await locator.count()
            return [locator.nth(i) for i in range(count)]
        return []

    @staticmethod
    async def safe_click(
        page: Page,
        selector: str,
        options: Optional[SelectorOptions] = None
    ) -> bool:
        try:
            element = await SmartSelector.find_element(page, selector, options)
            if element:
                await element.first.click(timeout=options.timeout if options else 10000)
                logger.success(f"Clicked element: {selector}")
                return True
        except Exception as e:
            logger.error(f"Failed to click element {selector}: {str(e)}")
        return False

    @staticmethod
    async def safe_fill(
        page: Page,
        selector: str,
        value: str,
        options: Optional[SelectorOptions] = None
    ) -> bool:
        try:
            element = await SmartSelector.find_element(page, selector, options)
            if element:
                await element.first.fill(value, timeout=options.timeout if options else 10000)
                logger.success(f"Filled element {selector} with value")
                return True
        except Exception as e:
            logger.error(f"Failed to fill element {selector}: {str(e)}")
        return False

    @staticmethod
    async def get_text(
        page: Page,
        selector: str,
        options: Optional[SelectorOptions] = None
    ) -> Optional[str]:
        try:
            element = await SmartSelector.find_element(page, selector, options)
            if element:
                text = await element.first.text_content(timeout=options.timeout if options else 10000)
                return text
        except Exception as e:
            logger.error(f"Failed to get text from {selector}: {str(e)}")
        return None

    @staticmethod
    async def get_attribute(
        page: Page,
        selector: str,
        attribute: str,
        options: Optional[SelectorOptions] = None
    ) -> Optional[str]:
        try:
            element = await SmartSelector.find_element(page, selector, options)
            if element:
                attr_value = await element.first.get_attribute(attribute, timeout=options.timeout if options else 10000)
                return attr_value
        except Exception as e:
            logger.error(f"Failed to get attribute {attribute} from {selector}: {str(e)}")
        return None

visionvault/services/dom_inspector.py.content:
"""
DOM Inspector Service
Analyzes web pages to extract element information for intelligent locator selection.
"""
import asyncio
import re
from typing import Dict, List, Optional
from playwright.async_api import async_playwright, Page


class DOMInspector:
    """Inspects DOM structure to help AI select perfect locators"""

    def __init__(self):
        self.browser = None
        self.context = None
        self.page = None

    async def analyze_page(self, url: str, user_command: str) -> Dict:
        """
        Navigate to a page and extract element information for intelligent locator selection.

        Args:
            url: The URL to analyze
            user_command: The user's natural language command to understand intent

        Returns:
            Dict containing available elements, their attributes, and recommended locators
        """
        try:
            async with async_playwright() as p:
                browser = await p.chromium.launch(headless=True)
                page = await browser.new_page()

                # Navigate to the page
                await page.goto(url, timeout=15000, wait_until='domcontentloaded')

                # Extract page information
                page_info = {
                    'url': url,
                    'title': await page.title(),
                    'interactive_elements': await self._extract_interactive_elements(page),
                    'form_elements': await self._extract_form_elements(page),
                    'navigation_elements': await self._extract_navigation_elements(page),
                    'intent_matched_elements': await self._match_user_intent(page, user_command)
                }

                await browser.close()
                return page_info

        except Exception as e:
            return {
                'error': str(e),
                'url': url,
                'message': 'Could not inspect DOM, AI will use standard locator strategies'
            }

    async def _extract_interactive_elements(self, page: Page) -> List[Dict]:
        """Extract all interactive elements (buttons, links, inputs)"""
        try:
            elements = await page.evaluate('''() => {
                const elements = [];

                // Buttons
                document.querySelectorAll('button, input[type="button"], input[type="submit"]').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {  // Only visible elements
                        elements.push({
                            type: 'button',
                            text: el.textContent.trim() || el.value || '',
                            role: el.getAttribute('role') || 'button',
                            name: el.getAttribute('aria-label') || el.textContent.trim() || el.value || '',
                            id: el.id,
                            testid: el.getAttribute('data-testid'),
                            classes: el.className
                        });
                    }
                });

                // Links
                document.querySelectorAll('a').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        elements.push({
                            type: 'link',
                            text: el.textContent.trim(),
                            href: el.href,
                            role: el.getAttribute('role') || 'link',
                            name: el.getAttribute('aria-label') || el.textContent.trim(),
                            id: el.id,
                            testid: el.getAttribute('data-testid')
                        });
                    }
                });

                return elements;
            }''')
            return elements
        except:
            return []

    async def _extract_form_elements(self, page: Page) -> List[Dict]:
        """Extract form inputs with their labels and attributes"""
        try:
            elements = await page.evaluate('''() => {
                const elements = [];

                // Text inputs, textareas
                document.querySelectorAll('input[type="text"], input[type="email"], input[type="password"], input[type="search"], input:not([type]), textarea').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        // Find associated label
                        let labelText = '';
                        if (el.id) {
                            const label = document.querySelector(`label[for="${el.id}"]`);
                            if (label) labelText = label.textContent.trim();
                        }
                        if (!labelText) {
                            const parentLabel = el.closest('label');
                            if (parentLabel) labelText = parentLabel.textContent.trim();
                        }

                        elements.push({
                            type: 'input',
                            inputType: el.type || 'text',
                            placeholder: el.placeholder,
                            label: labelText,
                            role: el.getAttribute('role') || (el.type === 'search' ? 'combobox' : 'textbox'),
                            name: el.getAttribute('aria-label') || labelText || el.placeholder || '',
                            id: el.id,
                            testid: el.getAttribute('data-testid'),
                            autocomplete: el.autocomplete
                        });
                    }
                });

                // Checkboxes and radios
                document.querySelectorAll('input[type="checkbox"], input[type="radio"]').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        let labelText = '';
                        if (el.id) {
                            const label = document.querySelector(`label[for="${el.id}"]`);
                            if (label) labelText = label.textContent.trim();
                        }

                        elements.push({
                            type: el.type,
                            label: labelText,
                            role: el.type,
                            name: el.getAttribute('aria-label') || labelText || '',
                            id: el.id,
                            testid: el.getAttribute('data-testid')
                        });
                    }
                });

                // Select dropdowns
                document.querySelectorAll('select').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        let labelText = '';
                        if (el.id) {
                            const label = document.querySelector(`label[for="${el.id}"]`);
                            if (label) labelText = label.textContent.trim();
                        }

                        elements.push({
                            type: 'select',
                            label: labelText,
                            role: 'combobox',
                            name: el.getAttribute('aria-label') || labelText || '',
                            id: el.id,
                            testid: el.getAttribute('data-testid')
                        });
                    }
                });

                return elements;
            }''')
            return elements
        except:
            return []

    async def _extract_navigation_elements(self, page: Page) -> List[Dict]:
        """Extract navigation elements (nav links, menus)"""
        try:
            elements = await page.evaluate('''() => {
                const elements = [];

                document.querySelectorAll('nav a, [role="navigation"] a').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        elements.push({
                            type: 'nav-link',
                            text: el.textContent.trim(),
                            href: el.href,
                            role: 'link',
                            name: el.textContent.trim()
                        });
                    }
                });

                return elements;
            }''')
            return elements
        except:
            return []

    async def _match_user_intent(self, page: Page, user_command: str) -> List[Dict]:
        """
        Match user intent to specific elements on the page.
        For example: "search for cats" -> find search box
        """
        command_lower = user_command.lower()
        matched_elements = []

        # Search intent
        if any(word in command_lower for word in ['search', 'find', 'look for', 'query']):
            search_elements = await page.evaluate('''() => {
                const elements = [];

                // Find search inputs
                document.querySelectorAll('input[type="search"], input[name*="search" i], input[placeholder*="search" i], input[aria-label*="search" i]').forEach(el => {
                    const rect = el.getBoundingClientRect();
                    if (rect.width > 0 && rect.height > 0) {
                        elements.push({
                            type: 'search-input',
                            placeholder: el.placeholder,
                            role: el.getAttribute('role') || 'combobox',
                            name: el.getAttribute('aria-label') || el.placeholder || 'Search',
                            testid: el.getAttribute('data-testid'),
                            id: el.id,
                            recommended_locator: el.getAttribute('aria-label') 
                                ? `get_by_role("combobox", name="${el.getAttribute('aria-label')}")`
                                : (el.placeholder ? `get_by_placeholder("${el.placeholder}")` : null)
                        });
                    }
                });

                return elements;
            }''')
            matched_elements.extend(search_elements)

        # Login intent
        if any(word in command_lower for word in ['login', 'log in', 'sign in', 'signin']):
            login_elements = await page.evaluate('''() => {
                const elements = [];

                // Find login buttons
                document.querySelectorAll('button, a, input[type="submit"]').forEach(el => {
                    const text = el.textContent.trim() || el.value || '';
                    if (text.match(/log ?in|sign ?in/i)) {
                        elements.push({
                            type: 'login-button',
                            text: text,
                            role: 'button',
                            name: text,
                            recommended_locator: `get_by_role("button", name="${text}")`
                        });
                    }
                });

                return elements;
            }''')
            matched_elements.extend(login_elements)

        # Submit intent
        if any(word in command_lower for word in ['submit', 'send', 'post']):
            submit_elements = await page.evaluate('''() => {
                const elements = [];

                document.querySelectorAll('button[type="submit"], input[type="submit"], button').forEach(el => {
                    const text = el.textContent.trim() || el.value || '';
                    if (text.match(/submit|send|post|continue|next/i)) {
                        elements.push({
                            type: 'submit-button',
                            text: text,
                            role: 'button',
                            name: text,
                            recommended_locator: `get_by_role("button", name="${text}")`
                        });
                    }
                });

                return elements;
            }''')
            matched_elements.extend(submit_elements)

        return matched_elements

    def generate_locator_recommendations(self, page_info: Dict) -> str:
        """
        Generate a detailed report of available locators for the AI to use.
        """
        if 'error' in page_info:
            return ""

        recommendations = []
        recommendations.append("=== REAL PAGE ANALYSIS ===")
        recommendations.append(f"Page: {page_info.get('title', 'Unknown')} ({page_info.get('url', '')})")
        recommendations.append("")

        # Intent-matched elements (highest priority)
        if page_info.get('intent_matched_elements'):
            recommendations.append("üéØ ELEMENTS MATCHING USER INTENT (Use these first!):")
            for elem in page_info['intent_matched_elements'][:5]:
                if elem.get('recommended_locator'):
                    recommendations.append(f"  ‚Ä¢ {elem['type']}: {elem.get('text', elem.get('name', ''))} ‚Üí {elem['recommended_locator']}")
                else:
                    recommendations.append(f"  ‚Ä¢ {elem['type']}: {elem.get('text', elem.get('name', ''))}")
            recommendations.append("")

        # Form elements
        if page_info.get('form_elements'):
            recommendations.append("üìù FORM ELEMENTS AVAILABLE:")
            for elem in page_info['form_elements'][:10]:
                locators = []
                if elem.get('testid'):
                    locators.append(f"get_by_test_id(\"{elem['testid']}\")")
                if elem.get('label'):
                    locators.append(f"get_by_label(\"{elem['label']}\")")
                if elem.get('placeholder'):
                    locators.append(f"get_by_placeholder(\"{elem['placeholder']}\")")
                if elem.get('role') and elem.get('name'):
                    locators.append(f"get_by_role(\"{elem['role']}\", name=\"{elem['name']}\")")

                if locators:
                    recommendations.append(f"  ‚Ä¢ {elem.get('type', 'input')} ({elem.get('inputType', '')}): {' OR '.join(locators[:2])}")
            recommendations.append("")

        # Interactive elements
        if page_info.get('interactive_elements'):
            recommendations.append("üîò BUTTONS & LINKS AVAILABLE:")
            for elem in page_info['interactive_elements'][:10]:
                locators = []
                if elem.get('testid'):
                    locators.append(f"get_by_test_id(\"{elem['testid']}\")")
                if elem.get('role') and elem.get('name'):
                    locators.append(f"get_by_role(\"{elem['role']}\", name=\"{elem['name']}\")")
                if elem.get('text'):
                    locators.append(f"get_by_text(\"{elem['text']}\", exact=True)")

                if locators:
                    recommendations.append(f"  ‚Ä¢ {elem.get('type', 'element')}: {locators[0]}")
            recommendations.append("")

        recommendations.append("‚úÖ USE THESE EXACT LOCATORS - They are confirmed to exist on the page!")
        recommendations.append("=" * 60)

        return "\n".join(recommendations)


