import os
import sys
import subprocess
import re
import base64


def detect_browsers():
    """Detect available browsers on the system"""
    browsers = []
    try:
        if sys.platform == 'win32':
            paths = [
                r"C:\Program Files\Google\Chrome\Application\chrome.exe",
                r"C:\Program Files (x86)\Google\Chrome\Application\chrome.exe"
            ]
            if any(os.path.exists(p) for p in paths):
                browsers.append('chromium')
        elif sys.platform == 'darwin':
            if os.path.exists('/Applications/Google Chrome.app'):
                browsers.append('chromium')
            if os.path.exists('/Applications/Firefox.app'):
                browsers.append('firefox')
            if os.path.exists('/Applications/Safari.app'):
                browsers.append('webkit')
        else:
            if subprocess.run(['which', 'google-chrome'], capture_output=True).returncode == 0:
                browsers.append('chromium')
            if subprocess.run(['which', 'firefox'], capture_output=True).returncode == 0:
                browsers.append('firefox')
        if not browsers:
            browsers = ['chromium']
    except Exception as e:
        print(f"Browser detection error: {e}")
        browsers = ['chromium']

    print(f"Detected browsers: {browsers}")
    return browsers


def extract_failed_locator_local(error_message):
    """Extract the failed locator from Playwright error messages."""
    patterns = [
        r'locator\(["\']([^"\']+)["\']\)',
        r'waiting for locator\(["\']([^"\']+)["\']\)',
        r'waiting for ([^\s]+)',
        r'Timeout.*?locator\(["\']([^"\']+)["\']\)',
    ]

    for pattern in patterns:
        match = re.search(pattern, error_message)
        if match:
            return match.group(1)

    return None


def encode_screenshot(screenshot_bytes):
    """Encode screenshot to base64 string"""
    if screenshot_bytes:
        return base64.b64encode(screenshot_bytes).decode('utf-8')
    return None

visionvault/services/healing_executor.py.content:
import asyncio
import json
import re
from visionvault.services.code_validator import CodeValidator
from openai import OpenAI
import os

class HealingExecutor:
    def __init__(self, socketio, api_key=None):
        self.socketio = socketio
        # Use provided API key or fallback to environment variable
        openai_key = api_key or os.environ.get('OPENAI_API_KEY')
        self.client = OpenAI(api_key=openai_key) if openai_key else None
        self.healed_script = None
        self.failed_locators = []
        self.retry_count = 0
        self.max_retries = 3
        self.user_selector_event = None
        self.user_selected_selector = None
        self.execution_mode = 'server'  # 'server' or 'agent'
        self.agent_result = None
        self.agent_result_event = None
        self.agent_sid = None  # Agent session ID for targeted emits

    def improve_locator_with_ai(self, failed_locator, error_message, page_html_snippet=''):
        """Use AI to suggest better locator strategies."""
        try:
            response = self.client.chat.completions.create(
                model="gpt-4o-mini",
                messages=[
                    {"role": "system", "content": """You are an expert at web automation and CSS/XPath selectors.
When a locator fails, suggest better, more robust alternatives. Consider:
1. Using text content locators when possible
2. Using data-testid or aria-labels
3. Using role-based selectors
4. Creating more specific CSS selectors
5. Using XPath as last resort

Return ONLY the improved locator string, nothing else."""},
                    {"role": "user", "content": f"""Failed locator: {failed_locator}
Error: {error_message}
Page context: {page_html_snippet[:500] if page_html_snippet else 'Not available'}

Suggest a better locator:"""}
                ],
                temperature=0.3
            )

            improved = response.choices[0].message.content.strip()
            if improved.startswith('```'):
                improved = improved.split('\n')[1]
            if improved.endswith('```'):
                improved = improved.rsplit('\n', 1)[0]

            return improved.strip('"\'')
        except Exception as e:
            print(f"AI locator improvement error: {e}")
            return failed_locator

    def heal_script(self, original_code, failed_locator, healed_locator):
        """Replace failed locator with healed one in the script."""
        print(f"\nüîß HEALING SCRIPT:")
        print(f"  Failed locator: '{failed_locator}'")
        print(f"  Healed locator: '{healed_locator}'")
        print(f"  Searching for '{failed_locator}' in code...")
        print(f"  Found: {failed_locator in original_code}")

        healed = original_code.replace(failed_locator, healed_locator)
        self.healed_script = healed

        print(f"  Replacement successful: {healed != original_code}")
        if healed != original_code:
            print(f"  Code changed from {len(original_code)} to {len(healed)} chars")
        else:
            print(f"  ‚ö†Ô∏è  WARNING: Code unchanged after replacement!")

        return healed

    async def wait_for_user_selector(self, timeout=300):
        """Wait for user to select an element interactively."""
        self.user_selector_event = asyncio.Event()
        self.user_selected_selector = None

        try:
            await asyncio.wait_for(self.user_selector_event.wait(), timeout=timeout)
            return self.user_selected_selector
        except asyncio.TimeoutError:
            return None

    def set_user_selector(self, selector):
        """Called when user selects an element."""
        self.user_selected_selector = selector
        if self.user_selector_event:
            self.user_selector_event.set()

    def set_agent_result(self, result):
        """Called when agent returns result."""
        self.agent_result = result
        if self.agent_result_event:
            self.agent_result_event.set()

    async def _execute_on_agent(self, code, browser_name, headless, test_id, attempt_num, logs):
        """Execute code on agent and wait for result."""
        import base64

        # Setup event to wait for agent result
        self.agent_result_event = asyncio.Event()
        self.agent_result = None

        # For headful mode, use modified code that keeps browser open
        execution_code = code
        if not headless:
            # Add a small delay to ensure browser is ready for widget injection
            execution_code = code.replace(
                "async def run_test(browser_name='chromium', headless=True):",
                "async def run_test(browser_name='chromium', headless=True):\n    import asyncio\n    await asyncio.sleep(1)  # Ensure browser is ready"
            )

        # Emit execution request to agent (targeted to specific agent)
        mode = 'headless' if headless else 'headful'
        if self.agent_sid:
            self.socketio.emit('execute_healing_attempt', {
                'test_id': test_id,
                'code': execution_code,
                'browser': browser_name,
                'mode': mode,
                'attempt': attempt_num + 1
            }, to=self.agent_sid)
        else:
            # Fallback to broadcast if no specific agent
            self.socketio.emit('execute_healing_attempt', {
                'test_id': test_id,
                'code': execution_code,
                'browser': browser_name,
                'mode': mode,
                'attempt': attempt_num + 1
            })

        # Wait for agent result with extended timeout for headful mode
        timeout = 180 if not headless else 120  # 3 minutes for headful, 2 for headless
        try:
            await asyncio.wait_for(self.agent_result_event.wait(), timeout=timeout)
        except asyncio.TimeoutError:
            return {
                'success': False,
                'logs': logs + ['‚ùå Agent execution timeout'],
                'screenshot': None,
                'can_heal': False
            }

        result = self.agent_result
        if not result:
            return {
                'success': False,
                'logs': logs + ['‚ùå No result from agent'],
                'screenshot': None,
                'can_heal': False
            }

        # Decode screenshot if present
        screenshot = None
        if result.get('screenshot'):
            try:
                screenshot = base64.b64decode(result['screenshot'])
            except:
                pass

        logs.extend(result.get('logs', []))

        if result.get('success'):
            logs.append("‚úÖ Execution completed successfully")
            return {
                'success': True,
                'logs': logs,
                'screenshot': screenshot
            }
        else:
            # Extract failed locator from error
            error_msg = ' '.join(result.get('logs', []))
            failed_locator = self.extract_failed_locator(error_msg)

            if failed_locator:
                self.failed_locators.append({
                    'locator': failed_locator,
                    'error': error_msg,
                    'attempt': attempt_num + 1
                })

                return {
                    'success': False,
                    'logs': logs,
                    'screenshot': screenshot,
                    'can_heal': True,
                    'failed_locator': failed_locator,
                    'error_message': error_msg,
                    'page_content': ''
                }
            else:
                return {
                    'success': False,
                    'logs': logs,
                    'screenshot': screenshot,
                    'can_heal': False
                }

    async def execute_with_healing(self, code, browser_name, headless, test_id):
        """Execute code with automatic healing and retry on failures."""
        validator = CodeValidator()
        if not validator.validate(code):
            return {
                'success': False,
                'logs': ['Security validation failed: ' + '; '.join(validator.get_errors())],
                'screenshot': None,
                'healed_script': None
            }

        self.healed_script = code
        current_code = code

        for attempt in range(self.max_retries):
            result = await self._execute_single_attempt(current_code, browser_name, headless, test_id, attempt)

            if result['success']:
                final_result = {
                    'success': True,
                    'logs': result['logs'],
                    'screenshot': result['screenshot'],
                    'healed_script': self.healed_script if self.healed_script != code else None,
                    'failed_locators': self.failed_locators
                }

                if self.failed_locators:
                    await self.report_failures_to_ai(test_id)
                    final_result['logs'].append("üìä AI analysis complete - check insights for improvement recommendations")

                return final_result

            if not result.get('can_heal'):
                return result

            failed_locator = result.get('failed_locator')
            import sys
            print(f"üîç DEBUG: failed_locator={failed_locator}, headless={headless}, execution_mode={self.execution_mode}", flush=True)
            sys.stdout.flush()

            if failed_locator:
                improved_locator = None

                if not headless:
                    mode = 'headful' if not headless else 'headless'
                    # Emit to specific agent
                    print(f"üîî SERVER: Emitting element_selector_needed event for test {test_id}, locator: {failed_locator}, mode: {mode}", flush=True)
                    sys.stdout.flush()

                    if self.agent_sid:
                        self.socketio.emit('element_selector_needed', {
                            'test_id': test_id,
                            'failed_locator': failed_locator,
                            'error': result.get('error_message', ''),
                            'attempt': attempt + 1,
                            'mode': mode
                        }, to=self.agent_sid)
                    else:
                        # Fallback to broadcast
                        self.socketio.emit('element_selector_needed', {
                            'test_id': test_id,
                            'failed_locator': failed_locator,
                            'error': result.get('error_message', ''),
                            'attempt': attempt + 1,
                            'mode': mode
                        })

                    print(f"‚úÖ SERVER: element_selector_needed event emitted successfully", flush=True)
                    sys.stdout.flush()

                    result['logs'].append(f"üëÜ Waiting for user to select element (failed locator: {failed_locator})...")

                    user_selector = await self.wait_for_user_selector(timeout=300)

                    if user_selector:
                        improved_locator = user_selector
                        print(f"\n‚úÖ USER SELECTED: '{improved_locator}'", flush=True)
                        result['logs'].append(f"‚úÖ User selected element: {improved_locator}")
                    else:
                        result['logs'].append(f"‚è±Ô∏è  User selection timeout, falling back to AI...")
                        improved_locator = self.improve_locator_with_ai(
                            failed_locator, 
                            result.get('error_message', ''),
                            result.get('page_content', '')
                        )
                        result['logs'].append(f"ü§ñ AI suggested locator: {improved_locator}")
                else:
                    self.socketio.emit('healing_required', {
                        'test_id': test_id,
                        'failed_locator': failed_locator,
                        'error': result.get('error_message', ''),
                        'attempt': attempt + 1,
                        'headless': headless
                    })

                    improved_locator = self.improve_locator_with_ai(
                        failed_locator, 
                        result.get('error_message', ''),
                        result.get('page_content', '')
                    )

                    result['logs'].append(f"üîß Healing attempt {attempt + 1}: AI suggested locator: {improved_locator}")

                current_code = self.heal_script(current_code, failed_locator, improved_locator)

                print(f"\nüì§ EMITTING script_healed event:")
                print(f"  test_id: {test_id}")
                print(f"  failed_locator: '{failed_locator}'")
                print(f"  healed_locator: '{improved_locator}'")
                print(f"  healed_script length: {len(current_code)}", flush=True)

                self.socketio.emit('script_healed', {
                    'test_id': test_id,
                    'healed_script': current_code,
                    'failed_locator': failed_locator,
                    'healed_locator': improved_locator,
                    'attempt': attempt + 1
                })

                await asyncio.sleep(0.5)
            else:
                return result

        print(f"\n‚ùå HEALING FAILED after {self.max_retries} attempts")
        print(f"  self.healed_script is None: {self.healed_script is None}")
        print(f"  self.healed_script length: {len(self.healed_script) if self.healed_script else 0}", flush=True)

        final_result = {
            'success': False,
            'logs': result.get('logs', []) + [f'‚ùå Failed after {self.max_retries} healing attempts'],
            'screenshot': result.get('screenshot'),
            'healed_script': self.healed_script,
            'failed_locators': self.failed_locators
        }

        if self.failed_locators:
            await self.report_failures_to_ai(test_id)

        return final_result

    async def _execute_single_attempt(self, code, browser_name, headless, test_id, attempt_num):
        """Execute a single attempt of the automation code."""
        logs = [f"‚ñ∂Ô∏è  Attempt {attempt_num + 1}: Executing automation..."]
        screenshot = None
        page_content = ''

        # If agent execution mode, delegate to agent
        if self.execution_mode == 'agent':
            return await self._execute_on_agent(code, browser_name, headless, test_id, attempt_num, logs)

        try:
            from playwright.async_api import TimeoutError as PlaywrightTimeout

            restricted_globals = {
                '__builtins__': {
                    'True': True, 'False': False, 'None': None,
                    'dict': dict, 'list': list, 'str': str, 'int': int,
                    'float': float, 'bool': bool, 'len': len,
                    'Exception': Exception, '__import__': __import__,
                }
            }

            local_vars = {}

            try:
                exec(code, restricted_globals, local_vars)

                if 'run_test' not in local_vars:
                    logs.append("‚ùå Error: Generated code must contain a run_test function")
                    return {
                        'success': False,
                        'logs': logs,
                        'screenshot': None,
                        'can_heal': False
                    }

                result = await local_vars['run_test'](browser_name=browser_name, headless=headless)
                logs.extend(result.get('logs', []))
                screenshot = result.get('screenshot')

                if result.get('success'):
                    logs.append("‚úÖ Execution completed successfully")
                    return {
                        'success': True,
                        'logs': logs,
                        'screenshot': screenshot
                    }
                else:
                    error_msg = ' '.join(result.get('logs', []))
                    failed_locator = self.extract_failed_locator(error_msg)

                    if failed_locator:
                        self.failed_locators.append({
                            'locator': failed_locator,
                            'error': error_msg,
                            'attempt': attempt_num + 1
                        })

                        return {
                            'success': False,
                            'logs': logs,
                            'screenshot': screenshot,
                            'can_heal': True,
                            'failed_locator': failed_locator,
                            'error_message': error_msg,
                            'page_content': page_content
                        }
                    else:
                        return {
                            'success': False,
                            'logs': logs,
                            'screenshot': screenshot,
                            'can_heal': False
                        }

            except PlaywrightTimeout as e:
                error_msg = str(e)
                logs.append(f"‚è±Ô∏è  Timeout error: {error_msg}")

                failed_locator = self.extract_failed_locator(error_msg)

                if failed_locator:
                    self.failed_locators.append({
                        'locator': failed_locator,
                        'error': error_msg,
                        'attempt': attempt_num + 1
                    })

                    return {
                        'success': False,
                        'logs': logs,
                        'screenshot': screenshot,
                        'can_heal': True,
                        'failed_locator': failed_locator,
                        'error_message': error_msg,
                        'page_content': page_content
                    }
                else:
                    return {
                        'success': False,
                        'logs': logs,
                        'screenshot': screenshot,
                        'can_heal': False
                    }

            except Exception as e:
                error_msg = str(e)
                logs.append(f"‚ùå Execution error: {error_msg}")

                failed_locator = self.extract_failed_locator(error_msg)

                if failed_locator:
                    self.failed_locators.append({
                        'locator': failed_locator,
                        'error': error_msg,
                        'attempt': attempt_num + 1
                    })

                    return {
                        'success': False,
                        'logs': logs,
                        'screenshot': screenshot,
                        'can_heal': True,
                        'failed_locator': failed_locator,
                        'error_message': error_msg,
                        'page_content': page_content
                    }
                else:
                    return {
                        'success': False,
                        'logs': logs,
                        'screenshot': screenshot,
                        'can_heal': False
                    }

        except Exception as e:
            logs.append(f'üí• Fatal error: {str(e)}')
            return {
                'success': False,
                'logs': logs,
                'screenshot': screenshot,
                'can_heal': False
            }

    def extract_failed_locator(self, error_message):
        """Extract the failed locator from error message."""
        patterns = [
            r'locator\("([^"]+)"\)',
            r'selector "([^"]+)"',
            r'element "([^"]+)"',
            r'get_by_text\("([^"]+)"\)',
            r'get_by_role\("([^"]+)"\)',
            r"locator\('([^']+)'\)",
            r"selector '([^']+)'",
        ]

        for pattern in patterns:
            match = re.search(pattern, error_message, re.IGNORECASE)
            if match:
                return match.group(1)

        return None

    async def report_failures_to_ai(self, test_id):
        """Report all failures and healing attempts to AI for continuous improvement."""
        if not self.failed_locators or not self.client:
            return

        try:
            failure_report = {
                'test_id': test_id,
                'total_failures': len(self.failed_locators),
                'failures': self.failed_locators,
                'healed_script': self.healed_script
            }

            response = self.client.chat.completions.create(
                model="gpt-4o-mini",
                messages=[
                    {"role": "system", "content": """You are an automation quality analyst. 
Analyze the failures and healing attempts to provide insights for improving automation scripts.
Identify patterns, suggest best practices, and recommend preventive measures."""},
                    {"role": "user", "content": f"""Analyze these automation failures and healing attempts:

Test ID: {test_id}
Total Failures: {len(self.failed_locators)}

Failures:
{json.dumps(self.failed_locators, indent=2)}

Final Healed Script:
{self.healed_script[:500] if self.healed_script else 'None'}

Provide:
1. Key insights about failure patterns
2. Recommendations for better locator strategies
3. Preventive measures for future scripts"""}
                ],
                temperature=0.3
            )

            insights = response.choices[0].message.content.strip()

            self.socketio.emit('ai_insights', {
                'test_id': test_id,
                'insights': insights,
                'failure_count': len(self.failed_locators)
            })

            return insights
        except Exception as e:
            print(f"AI feedback error: {e}")
            return None

visionvault/agents/recording_session.py.content:
import asyncio
import os
import subprocess
from .config import AGENT_ID, SERVER_URL

class CodegenRecordingSessionManager:
    def __init__(self, socket_client):
        self.socket_client = socket_client
        self.sessions = {}  # session_id -> subprocess info

    async def start_recording_session(self, session_id: str, start_url: str = ""):
        """Start a Playwright codegen session in a subprocess."""
        if session_id in self.sessions:
            print(f"Session {session_id} is already running")
            return

        os.makedirs("recordings", exist_ok=True)
        output_file = os.path.join("recordings", f"{session_id}.py")

        cmd = [
            "playwright",
            "codegen",
            "--target=python",
            "--output", output_file
        ]
        if start_url:
            cmd.append(start_url)

        print(f"üé¨ Starting codegen session {session_id}")
        process = subprocess.Popen(cmd, stdout=subprocess.PIPE, stderr=subprocess.PIPE)

        self.sessions[session_id] = {
            "process": process,
            "output_file": output_file,
            "start_url": start_url
        }

        self.socket_client.emit("recording_status", {
            "session_id": session_id,
            "status": "started",
            "output_file": output_file
        })

        asyncio.create_task(self._monitor_process(session_id))

    async def _monitor_process(self, session_id: str):
        """Monitor the recording process and auto-stop when browser closes."""
        session = self.sessions.get(session_id)
        if not session:
            return

        process = session["process"]

        await asyncio.get_event_loop().run_in_executor(None, process.wait)

        print(f"üîî Browser closed for session {session_id}. Auto-stopping recording...")
        await self.stop_recording_session(session_id, auto_stopped=True)

    async def stop_recording_session(self, session_id: str, auto_stopped: bool = False):
        """Stop the codegen subprocess and read the generated file."""
        session = self.sessions.get(session_id)
        if not session:
            print(f"Session {session_id} not found")
            return

        process = session["process"]
        output_file = session["output_file"]

        if process.poll() is None:
            process.terminate()
            try:
                await asyncio.get_event_loop().run_in_executor(None, lambda: process.wait(timeout=5))
            except subprocess.TimeoutExpired:
                process.kill()

        await asyncio.sleep(0.5)

        playwright_code = None
        actions = []

        if os.path.exists(output_file):
            try:
                with open(output_file, 'r') as f:
                    playwright_code = f.read()

                actions = self._extract_actions_from_code(playwright_code)
                print(f"‚úÖ Extracted {len(actions)} actions from recording")
            except Exception as e:
                print(f"‚ö†Ô∏è Error reading recording file: {e}")

        self.socket_client.emit("recording_status", {
            "session_id": session_id,
            "status": "stopped",
            "output_file": output_file,
            "playwright_code": playwright_code,
            "actions": actions,
            "auto_stopped": auto_stopped
        })

        print(f"‚úÖ Recording session {session_id} {'auto-' if auto_stopped else ''}stopped. Output: {output_file}")
        del self.sessions[session_id]

    def _extract_actions_from_code(self, code: str) -> list:
        """Extract human-readable actions with locators from generated Playwright code."""
        actions = []
        lines = code.split('\n')

        for line in lines:
            line = line.strip()

            if 'page.goto(' in line:
                import re
                match = re.search(r'page\.goto\(["\']([^"\']+)["\']', line)
                if match:
                    actions.append({
                        'action': 'Navigate',
                        'locator': match.group(1),
                        'description': f'Navigate to {match.group(1)}'
                    })

            elif 'page.click(' in line:
                import re
                match = re.search(r'page\.click\(["\']([^"\']+)["\']', line)
                if match:
                    actions.append({
                        'action': 'Click',
                        'locator': match.group(1),
                        'description': f'Click element: {match.group(1)}'
                    })

            elif 'page.fill(' in line:
                import re
                match = re.search(r'page\.fill\(["\']([^"\']+)["\'],\s*["\']([^"\']*)["\']', line)
                if match:
                    actions.append({
                        'action': 'Type',
                        'locator': match.group(1),
                        'value': match.group(2),
                        'description': f'Type "{match.group(2)}" into {match.group(1)}'
                    })

            elif 'page.select_option(' in line:
                import re
                match = re.search(r'page\.select_option\(["\']([^"\']+)["\'],\s*["\']([^"\']*)["\']', line)
                if match:
                    actions.append({
                        'action': 'Select',
                        'locator': match.group(1),
                        'value': match.group(2),
                        'description': f'Select option "{match.group(2)}" in {match.group(1)}'
                    })

            elif 'page.check(' in line:
                import re
                match = re.search(r'page\.check\(["\']([^"\']+)["\']', line)
                if match:
                    actions.append({
                        'action': 'Check',
                        'locator': match.group(1),
                        'description': f'Check checkbox: {match.group(1)}'
                    })

            elif 'page.press(' in line or 'page.keyboard.press(' in line:
                import re
                match = re.search(r'press\(["\']([^"\']+)["\']', line)
                if match:
                    actions.append({
                        'action': 'Press Key',
                        'locator': match.group(1),
                        'description': f'Press key: {match.group(1)}'
                    })

        return actions

visionvault/services/action_recorder.py.content:
import asyncio
from datetime import datetime
from typing import List, Dict, Optional
from playwright.async_api import Page, Browser, async_playwright
import uuid


class ActionRecorder:
    """Records browser actions for teaching mode."""

    def __init__(self):
        self.actions: List[Dict] = []
        self.is_recording = False
        self.page: Optional[Page] = None
        self.browser: Optional[Browser] = None
        self.playwright_instance = None

    async def start_recording(self, browser_name='chromium', headless=False):
        """Start recording browser actions."""
        self.actions = []
        self.is_recording = True
        self.playwright_instance = await async_playwright().start()
        browser_type = getattr(self.playwright_instance, browser_name)
        self.browser = await browser_type.launch(headless=headless)
        self.page = await self.browser.new_page()

        # Use add_init_script for persistent listeners
        await self.page.add_init_script(self._get_event_listener_js())
        await self.page.expose_function('recordUserAction', self._handle_user_action)
        self.page.on('framenavigated', lambda frame: asyncio.create_task(self._on_navigation(frame)))
        self.page.on('close', lambda: self._on_page_close())
        return self.page

    def _get_event_listener_js(self):
        # JS to listen for click, input, change, keyboard events and call recordUserAction
        return '''
        (function() {
            // Use WeakMap to track debounce timers by element identity, not selector
            const inputTimers = new WeakMap();
            const DEBOUNCE_DELAY = 500; // Wait 500ms after last keystroke

            function getSelector(el) {
                let selector = el.tagName.toLowerCase();
                if (el.id) {
                    selector += '#' + el.id;
                } else if (el.className && typeof el.className === 'string') {
                    const classes = el.className.split(' ').filter(c => c.trim());
                    if (classes.length > 0) {
                        selector += '.' + classes.join('.');  // Include ALL classes for specificity
                    }
                }
                return selector;
            }

            function sendAction(type, selector, value, text, key) {
                if (window.recordUserAction) {
                    const action = {type, selector, value, text, timestamp: new Date().toISOString()};
                    if (key) action.key = key;
                    window.recordUserAction(action);
                }
            }

            // Record clicks
            document.addEventListener('click', function(e) {
                const selector = getSelector(e.target);
                const text = e.target.innerText ? e.target.innerText.substring(0, 50) : null;
                sendAction('click', selector, null, text);
            }, true);

            // Record input with debouncing - track by element identity using WeakMap
            document.addEventListener('input', function(e) {
                const el = e.target;
                const selector = getSelector(el);

                // Clear existing timer for THIS SPECIFIC element
                if (inputTimers.has(el)) {
                    clearTimeout(inputTimers.get(el));
                }

                // Set new timer - will only fire if user stops typing for DEBOUNCE_DELAY ms
                const timer = setTimeout(() => {
                    sendAction('fill', selector, el.value, null);
                    inputTimers.delete(el);
                }, DEBOUNCE_DELAY);

                inputTimers.set(el, timer);
            }, true);

            // Record change events (for selects, checkboxes, radios)
            document.addEventListener('change', function(e) {
                const el = e.target;
                const selector = getSelector(el);

                if (el.type === 'checkbox' || el.type === 'radio') {
                    sendAction('check', selector, el.checked, null);
                } else if (el.tagName.toLowerCase() === 'select') {
                    sendAction('select', selector, el.value, null);
                }
            }, true);

            // Record keyboard events (Enter, Tab, Escape, etc.)
            document.addEventListener('keydown', function(e) {
                // Only record special keys, not regular characters (those are captured via input)
                const specialKeys = ['Enter', 'Tab', 'Escape', 'ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'];

                if (specialKeys.includes(e.key)) {
                    const el = e.target;
                    const selector = getSelector(el);

                    // If Enter key and there's a pending input timer, send it immediately
                    if (e.key === 'Enter' && inputTimers.has(el)) {
                        clearTimeout(inputTimers.get(el));
                        sendAction('fill', selector, el.value, null);
                        inputTimers.delete(el);
                    }

                    sendAction('press', selector, null, null, e.key);
                }
            }, true);

            // Record form submissions
            document.addEventListener('submit', function(e) {
                const selector = getSelector(e.target);
                sendAction('submit', selector, null, null);
            }, true);
        })();
        '''

    async def _on_navigation(self, frame):
        """Record navigation events."""
        if frame == self.page.main_frame:
            self.record_action({
                'type': 'navigate',
                'url': frame.url,
                'timestamp': datetime.now().isoformat()
            })
            # Re-expose function after navigation
            try:
                await self.page.expose_function('recordUserAction', self._handle_user_action)
            except Exception as e:
                pass

    def _on_page_close(self):
        self.is_recording = False

    async def _handle_user_action(self, action):
        # Receives actions from JS and records them
        self.record_action(action)

    def record_action(self, action: Dict):
        """Record an action with deduplication."""
        if self.is_recording:
            # Deduplicate consecutive identical actions
            if self.actions and self._is_duplicate_action(self.actions[-1], action):
                return  # Skip duplicate
            self.actions.append(action)

    def _is_duplicate_action(self, action1: Dict, action2: Dict) -> bool:
        """Check if two actions are duplicates - only deduplicate identical fills and navigation."""
        action_type = action1.get('type')

        # Different types are never duplicates
        if action_type != action2.get('type'):
            return False

        # For fill actions: same selector AND same value = duplicate
        if action_type == 'fill':
            return (action1.get('selector') == action2.get('selector') and 
                    action1.get('value') == action2.get('value'))

        # For navigation: same URL = duplicate
        if action_type in ('navigate', 'goto'):
            return action1.get('url') == action2.get('url')

        # Allow all other actions (clicks, presses, etc.) - user may intentionally repeat them
        return False

    def record_goto(self, url: str):
        """Record a goto action."""
        self.record_action({
            'type': 'goto',
            'url': url,
            'timestamp': datetime.now().isoformat()
        })

    def record_click(self, selector: str, text: Optional[str] = None):
        """Record a click action."""
        self.record_action({
            'type': 'click',
            'selector': selector,
            'text': text,
            'timestamp': datetime.now().isoformat()
        })

    def record_fill(self, selector: str, value: str):
        """Record a fill/input action."""
        self.record_action({
            'type': 'fill',
            'selector': selector,
            'value': value,
            'timestamp': datetime.now().isoformat()
        })

    def record_select(self, selector: str, value: str):
        """Record a select action."""
        self.record_action({
            'type': 'select',
            'selector': selector,
            'value': value,
            'timestamp': datetime.now().isoformat()
        })

    def record_check(self, selector: str):
        """Record a checkbox/radio check action."""
        self.record_action({
            'type': 'check',
            'selector': selector,
            'timestamp': datetime.now().isoformat()
        })

    def record_wait(self, wait_type: str, selector: Optional[str] = None, timeout: int = 5000):
        """Record a wait action."""
        action = {
            'type': 'wait',
            'wait_type': wait_type,  # 'navigation', 'selector', 'timeout'
            'timeout': timeout,
            'timestamp': datetime.now().isoformat()
        }
        if selector:
            action['selector'] = selector
        self.record_action(action)

    async def stop_recording(self):
        """Stop recording and return captured actions."""
        self.is_recording = False

        # Close browser
        if self.browser:
            await self.browser.close()
        if self.playwright_instance:
            await self.playwright_instance.stop()

        return self.actions

    def generate_playwright_code(self, actions: Optional[List[Dict]] = None) -> str:
        """Generate Playwright code from recorded actions."""
        if actions is None:
            actions = self.actions

        if not actions:
            return ""

        # Start building the code
        code_lines = [
            "async def run_test(browser_name='chromium', headless=True):",
            "    from playwright.async_api import async_playwright",
            "    logs = []",
            "    screenshot = None",
            "    ",
            "    try:",
            "        async with async_playwright() as p:",
            "            browser = await getattr(p, browser_name).launch(headless=headless)",
            "            page = await browser.new_page()",
            "            ",
        ]

        # Convert actions to code
        for i, action in enumerate(actions):
            action_type = action.get('type')

            if action_type == 'goto' or action_type == 'navigate':
                url = action.get('url')
                code_lines.append(f"            await page.goto('{url}')")
                code_lines.append(f"            logs.append('Navigated to {url}')")

            elif action_type == 'click':
                selector = action.get('selector')
                code_lines.append(f"            await page.click('{selector}')")
                code_lines.append(f"            logs.append('Clicked {selector}')")

            elif action_type == 'fill':
                selector = action.get('selector')
                value = action.get('value', '').replace("'", "\\'")
                code_lines.append(f"            await page.fill('{selector}', '{value}')")
                code_lines.append(f"            logs.append('Filled {selector}')")

            elif action_type == 'select':
                selector = action.get('selector')
                value = action.get('value')
                code_lines.append(f"            await page.select_option('{selector}', '{value}')")
                code_lines.append(f"            logs.append('Selected option in {selector}')")

            elif action_type == 'check':
                selector = action.get('selector')
                code_lines.append(f"            await page.check('{selector}')")
                code_lines.append(f"            logs.append('Checked {selector}')")

            elif action_type == 'wait':
                wait_type = action.get('wait_type')
                if wait_type == 'navigation':
                    code_lines.append("            await page.wait_for_load_state('networkidle')")
                    code_lines.append("            logs.append('Waited for navigation')")
                elif wait_type == 'selector':
                    selector = action.get('selector')
                    timeout = action.get('timeout', 5000)
                    code_lines.append(f"            await page.wait_for_selector('{selector}', timeout={timeout})")
                    code_lines.append(f"            logs.append('Waited for {selector}')")
                elif wait_type == 'timeout':
                    timeout = action.get('timeout', 1000)
                    code_lines.append(f"            await page.wait_for_timeout({timeout})")
                    code_lines.append(f"            logs.append('Waited {timeout}ms')")

            elif action_type == 'press':
                selector = action.get('selector')
                key = action.get('key', 'Enter')
                code_lines.append(f"            await page.press('{selector}', '{key}')")
                code_lines.append(f"            logs.append('Pressed {key} on {selector}')")

            elif action_type == 'submit':
                selector = action.get('selector')
                code_lines.append(f"            await page.locator('{selector}').press('Enter')")
                code_lines.append(f"            logs.append('Submitted form {selector}')")

        # Add screenshot and closing code
        code_lines.extend([
            "            ",
            "            # Take screenshot before closing",
            "            screenshot = await page.screenshot()",
            "            logs.append('Screenshot captured')",
            "            ",
            "            await browser.close()",
            "            return {'success': True, 'logs': logs, 'screenshot': screenshot}",
            "    ",
            "    except Exception as e:",
            "        logs.append(f'Error: {str(e)}')",
            "        if 'page' in locals():",
            "            try:",
            "                screenshot = await page.screenshot()",
            "            except:",
            "                pass",
            "        if 'browser' in locals():",
            "            try:",
            "                await browser.close()",
            "            except:",
            "                pass",
            "        return {'success': False, 'logs': logs, 'screenshot': screenshot}"
        ])

        return "\n".join(code_lines)

    @staticmethod
    def parse_code_to_actions(playwright_code: str) -> List[Dict]:
        """
        Parse Playwright code to extract actions (reverse operation).
        This is a simple parser that looks for common patterns.
        """
        actions = []
        lines = playwright_code.split('\n')

        for line in lines:
            line = line.strip()

            # Parse goto
            if 'page.goto(' in line:
                import re
                match = re.search(r"page\.goto\(['\"](.+?)['\"]\)", line)
                if match:
                    actions.append({
                        'type': 'goto',
                        'url': match.group(1)
                    })

            # Parse click
            elif 'page.click(' in line:
                import re
                match = re.search(r"page\.click\(['\"](.+?)['\"]\)", line)
                if match:
                    actions.append({
                        'type': 'click',
                        'selector': match.group(1)
                    })

            # Parse fill
            elif 'page.fill(' in line:
                import re
                match = re.search(r"page\.fill\(['\"](.+?)['\"],\s*['\"](.+?)['\"]\)", line)
                if match:
                    actions.append({
                        'type': 'fill',
                        'selector': match.group(1),
                        'value': match.group(2)
                    })

            # Add more parsers as needed

        return actions


class InteractiveRecorder(ActionRecorder):
    """
    Enhanced recorder that can intercept and record actual user interactions.
    This would be used with a UI where users can click through a task.
    """

    async def start_interactive_recording(self, browser_name='chromium'):
        """Start interactive recording with visible browser."""
        page = await self.start_recording(browser_name, headless=False)

        # Inject improved JavaScript with debouncing and keyboard capture
        await page.add_init_script("""
            window.__recordedActions = [];
            const inputTimers = new WeakMap();  // Track by element identity, not selector
            const DEBOUNCE_DELAY = 500;

            // Helper to generate CSS selector (must be defined first!)
            function getSelector(element) {
                let selector = element.tagName.toLowerCase();
                if (element.id) {
                    selector += '#' + element.id;
                } else if (element.className && typeof element.className === 'string') {
                    const classes = element.className.split(' ').filter(c => c.trim());
                    if (classes.length > 0) {
                        selector += '.' + classes.join('.');  // Include ALL classes for specificity
                    }
                }
                return selector;
            }

            // Record clicks
            document.addEventListener('click', (e) => {
                const selector = getSelector(e.target);
                window.__recordedActions.push({
                    type: 'click',
                    selector: selector,
                    text: e.target.textContent ? e.target.textContent.trim().substring(0, 50) : '',
                    timestamp: new Date().toISOString()
                });
                console.log('üé¨ Recorded click:', selector);
            }, true);

            // Record input with debouncing - track by element identity using WeakMap
            document.addEventListener('input', (e) => {
                const el = e.target;
                const selector = getSelector(el);

                if (inputTimers.has(el)) {
                    clearTimeout(inputTimers.get(el));
                }

                const timer = setTimeout(() => {
                    window.__recordedActions.push({
                        type: 'fill',
                        selector: selector,
                        value: el.value,
                        timestamp: new Date().toISOString()
                    });
                    console.log('üé¨ Recorded fill:', selector, '=', el.value);
                    inputTimers.delete(el);
                }, DEBOUNCE_DELAY);

                inputTimers.set(el, timer);
            }, true);

            // Record keyboard events
            document.addEventListener('keydown', (e) => {
                const specialKeys = ['Enter', 'Tab', 'Escape', 'ArrowUp', 'ArrowDown', 'ArrowLeft', 'ArrowRight'];

                if (specialKeys.includes(e.key)) {
                    const el = e.target;
                    const selector = getSelector(el);

                    // If Enter and there's pending input, send it immediately
                    if (e.key === 'Enter' && inputTimers.has(el)) {
                        clearTimeout(inputTimers.get(el));
                        window.__recordedActions.push({
                            type: 'fill',
                            selector: selector,
                            value: el.value,
                            timestamp: new Date().toISOString()
                        });
                        console.log('üé¨ Recorded fill (Enter):', selector, '=', el.value);
                        inputTimers.delete(el);
                    }

                    window.__recordedActions.push({
                        type: 'press',
                        selector: selector,
                        key: e.key,
                        timestamp: new Date().toISOString()
                    });
                    console.log('üé¨ Recorded keypress:', e.key, 'on', selector);
                }
            }, true);

            console.log('‚úÖ VisionVault recording initialized - actions will be captured automatically');
        """)

        return page

    async def get_recorded_actions_from_page(self):
        """Get actions recorded by JavaScript injection."""
        if not self.page:
            return []

        try:
            js_actions = await self.page.evaluate("window.__recordedActions || []")
            return js_actions
        except:
            return []

visionvault/services/code_validator.py.content:
import ast
import re

class CodeValidator:
    ALLOWED_IMPORTS = {
        'playwright.async_api',
        'asyncio',
        'time',
        'datetime',
        're',
        'json',
        'base64'
    }

    DANGEROUS_MODULES = {
        'os', 'sys', 'subprocess', 'shutil', 'eval', 'exec',
        'compile', '__import__', 'open', 'file', 'input',
        'execfile', 'reload', 'importlib', 'pickle', 'shelve',
        'socket', 'urllib', 'requests', 'http', 'ftplib',
        'telnetlib', 'smtplib', 'poplib', 'imaplib'
    }

    def __init__(self):
        self.errors = []

    def validate(self, code):
        self.errors = []

        if not code or not isinstance(code, str):
            self.errors.append("Code must be a non-empty string")
            return False

        if not self._check_function_structure(code):
            return False

        if not self._check_dangerous_imports(code):
            return False

        if not self._check_dangerous_patterns(code):
            return False

        try:
            tree = ast.parse(code)
            if not self._validate_ast(tree):
                return False
        except SyntaxError as e:
            self.errors.append(f"Syntax error: {str(e)}")
            return False

        return True

    def _check_function_structure(self, code):
        if 'async def run_test' not in code:
            self.errors.append("Code must contain 'async def run_test' function")
            return False

        if 'playwright.async_api import async_playwright' not in code:
            self.errors.append("Code must use 'from playwright.async_api import async_playwright'")
            return False

        return True

    def _check_dangerous_imports(self, code):
        for module in self.DANGEROUS_MODULES:
            patterns = [
                f'import {module}',
                f'from {module}',
                f'__import__("{module}")',
                f"__import__('{module}')"
            ]
            for pattern in patterns:
                if pattern in code:
                    self.errors.append(f"Dangerous import detected: {module}")
                    return False

        return True

    def _check_dangerous_patterns(self, code):
        dangerous_patterns = [
            (r'\beval\s*\(', 'eval() function'),
            (r'\bexec\s*\(', 'exec() function'),
            (r'\b__import__\s*\(', '__import__() function'),
            (r'\bcompile\s*\(', 'compile() function'),
            (r'\bopen\s*\(', 'open() function (file access)'),
            (r'\.system\s*\(', 'system() call'),
            (r'\.popen\s*\(', 'popen() call'),
            (r'\.spawn\s*\(', 'spawn() call'),
        ]

        for pattern, name in dangerous_patterns:
            if re.search(pattern, code, re.IGNORECASE):
                self.errors.append(f"Dangerous pattern detected: {name}")
                return False

        return True

    def _validate_ast(self, tree):
        for node in ast.walk(tree):
            if isinstance(node, ast.Import):
                for alias in node.names:
                    if not self._is_allowed_import(alias.name):
                        self.errors.append(f"Disallowed import: {alias.name}")
                        return False

            elif isinstance(node, ast.ImportFrom):
                if node.module and not self._is_allowed_import(node.module):
                    self.errors.append(f"Disallowed import from: {node.module}")
                    return False

            elif isinstance(node, ast.Call):
                if isinstance(node.func, ast.Name):
                    if node.func.id in ['eval', 'exec', 'compile', '__import__', 'open']:
                        self.errors.append(f"Dangerous function call: {node.func.id}")
                        return False

        return True

    def _is_allowed_import(self, module_name):
        for allowed in self.ALLOWED_IMPORTS:
            if module_name.startswith(allowed):
                return True
        return False

    def get_errors(self):
        return self.errors

visionvault/agents/test_executor.py.content:
import asyncio
from .utils import encode_screenshot


class TestExecutor:
    def __init__(self, socket_client):
        self.socket_client = socket_client

    async def execute_test(self, test_id, code, browser_name, mode):
        """Execute a test case"""
        headless = mode == 'headless'

        try:
            self.socket_client.emit('agent_log', {
                'test_id': test_id,
                'message': f'Preparing to execute test in {mode} mode...'
            })

            local_vars = {}
            exec(code, {}, local_vars)

            if 'run_test' not in local_vars:
                self.socket_client.emit('agent_result', {
                    'test_id': test_id,
                    'success': False,
                    'logs': ['Error: run_test missing'],
                    'screenshot': None
                })
                return

            run_test = local_vars['run_test']
            result = await run_test(browser_name=browser_name, headless=headless)

            screenshot_b64 = encode_screenshot(result.get('screenshot'))

            self.socket_client.emit('agent_result', {
                'test_id': test_id,
                'success': result.get('success', False),
                'logs': result.get('logs', []),
                'screenshot': screenshot_b64
            })

            print(f"Test {test_id} completed: {'SUCCESS' if result.get('success') else 'FAILED'}")

        except Exception as e:
            print(f"Execution error: {e}")
            self.socket_client.emit('agent_result', {
                'test_id': test_id,
                'success': False,
                'logs': [str(e)],
                'screenshot': None
            })

